// src/menu/Preferencias/Preferencias.jsx
import React, { useState, useEffect } from 'react';
import { Container, Card, Form, Button, Alert, Row, Col } from 'react-bootstrap';

// --- Tus listas de "botones amigables" ---
// Puedes expandir estas listas tanto como quieras
const PREDEFINED_LIKES = [
    'Pollo', 'Res', 'Pescado', 'Cerdo', 'Pasta', 'Arroz', 'Ensaladas', 'Sopas'
];
const PREDEFINED_DISLIKES = [
    'Cebolla', 'Cilantro', 'Brócoli', 'Picante', 'Lácteos', 'Huevo'
];
// -------------------------------------------


function Preferencias() {
    // Usamos 'Set' para manejar fácilmente los botones "prendidos"
    const [likes, setLikes] = useState(new Set());
    const [dislikes, setDislikes] = useState(new Set());
    const [customNotes, setCustomNotes] = useState('');
    
    const [status, setStatus] = useState({ loading: true, error: '', success: '' });

    // 1. Cargar datos existentes cuando el componente se monta
    useEffect(() => {
        const loadData = async () => {
            const userId = localStorage.getItem('IdUser');
            if (!userId) {
                setStatus({ loading: false, error: 'No se pudo encontrar el ID de usuario.', success: '' });
                return;
            }

            try {
                const response = await fetch(`http://localhost:3111/getPreferences?userId=${userId}`);
                if (!response.ok) throw new Error('Error al cargar datos.');

                const data = await response.json();
                
                // Si hay datos guardados, actualizamos el estado
                // Usamos `data.structured_likes || []` para evitar errores si es la primera vez
                setLikes(new Set(data.structured_likes || []));
                setDislikes(new Set(data.structured_dislikes || []));
                setCustomNotes(data.custom_notes || '');
                
                setStatus({ loading: false, error: '', success: '' });

            } catch (err) {
                setStatus({ loading: false, error: 'Error de red al cargar preferencias.', success: '' });
            }
        };

        loadData();
    }, []); // El array vacío asegura que esto solo se ejecute UNA VEZ

    // 2. Función para manejar el clic en los botones amigables
    const handleToggle = (item, type) => {
        // 'type' será 'likes' o 'dislikes'
        const stateSet = (type === 'likes') ? likes : dislikes;
        const setState = (type === 'likes') ? setLikes : setDislikes;
        
        // Creamos una nueva copia del Set para que React detecte el cambio
        const newSet = new Set(stateSet);
        if (newSet.has(item)) {
            newSet.delete(item); // Si ya estaba, lo quita
        } else {
            newSet.add(item); // Si no estaba, lo añade
        }
        setState(newSet);
    };

    // 3. Función para guardar todo en la base de datos
    const handleSave = async (e) => {
        e.preventDefault();
        setStatus({ loading: true, error: '', success: '' });

        const userId = localStorage.getItem('IdUser');
        
        // Construimos el objeto JSON exacto que planeamos
        const dataToSave = {
            structured_likes: [...likes], // Convertimos el Set a un Array
            structured_dislikes: [...dislikes], // Convertimos el Set a un Array
            custom_notes: customNotes
        };

        try {
            const response = await fetch('http://localhost:3111/savePreferences', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    userId: userId,
                    preferences: dataToSave
                })
            });

            if (!response.ok) throw new Error('No se pudo guardar.');
            
            setStatus({ loading: false, error: '', success: '¡Preferencias guardadas con éxito!' });

        } catch (err) {
            setStatus({ loading: false, error: 'Error de red al guardar.', success: '' });
        }
    };

    return (
        <Container className="mt-4">
            <Card>
                <Card.Body>
                    <Card.Title>Mi Perfil Culinario ⚙️</Card.Title>
                    <Card.Text>
                        Selecciona tus gustos y disgustos. Esto ayudará al Asistente IA 
                        a darte las mejores recomendaciones.
                    </Card.Text>

                    <Form onSubmit={handleSave}>
                        {/* SECCIÓN DE GUSTOS */}
                        <Form.Group className="mb-3">
                            <Form.Label as="h5">¿Qué te gusta?</Form.Label>
                            <div className="p-2 bg-light border rounded">
                                {PREDEFINED_LIKES.map(item => (
                                    <Button
                                        key={item}
                                        variant={likes.has(item) ? 'success' : 'outline-secondary'} // "Prendido" o "Apagado"
                                        onClick={() => handleToggle(item, 'likes')}
                                        className="m-1"
                                    >
                                        {item}
                                    </Button>
                                ))}
                            </div>
                        </Form.Group>

                        {/* SECCIÓN DE DISGUSTOS */}
                        <Form.Group className="mb-3">
                            <Form.Label as="h5">¿Qué no te gusta?</Form.Label>
                            <div className="p-2 bg-light border rounded">
                                {PREDEFINED_DISLIKES.map(item => (
                                    <Button
                                        key={item}
                                        variant={dislikes.has(item) ? 'danger' : 'outline-secondary'}
                                        onClick={() => handleToggle(item, 'dislikes')}
                                        className="m-1"
                                    >
                                        {item}
                                    </Button>
                                ))}
                            </div>
                        </Form.Group>
                        
                        {/* TU CAJA DE TEXTO PEQUEÑA */}
                        <Form.Group className="mb-3">
                            <Form.Label as="h5">Otras preferencias (Opcional)</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={3}
                                placeholder="Ej: Me gusta la comida muy especiada, sin picante, prefiero cocinar al vapor..."
                                value={customNotes}
                                onChange={(e) => setCustomNotes(e.target.value)}
                            />
                            <Form.Text className="text-muted">
                                El Asistente IA usará estas notas y podrá añadir más 
                                si aprende algo nuevo de ti.
                            </Form.Text>
                        </Form.Group>
                        
                        <hr />

                        {/* Botón de Guardar y Alertas */}
                        <div className="d-flex align-items-center">
                            <Button variant="primary" type="submit" disabled={status.loading}>
                                {status.loading ? 'Guardando...' : 'Guardar Perfil Culinario'}
                            </Button>
                            {status.success && <Alert variant="success" className="ms-3 mb-0 p-2">{status.success}</Alert>}
                            {status.error && <Alert variant="danger" className="ms-3 mb-0 p-2">{status.error}</Alert>}
                        </div>
                    </Form>

                </Card.Body>
            </Card>
        </Container>
    );
}

export default Preferencias;
